import React, { useState, useEffect } from "react";
import "../styles/sidebar.css";
import { fetchUserData, fetchUserExceptCurrent } from "./userStore";
import api from "../Api";

const Sidebar = ({ onUserSelect, setOnUserSelected }) => {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [recentMessages, setRecentMessages] = useState({});
  const [loggedInUser, setLoggedInUser] = useState("");

  useEffect(() => {
    const accessToken = localStorage.getItem("accessToken");

    const getUserData = async () => {
      try {
        const { _id } = await fetchUserData(accessToken);
        setLoggedInUser({ _id });
      } catch (error) {
        console.log("Error fetching username:", error);
      }
    };
    getUserData();
  }, []);

  useEffect(() => {
    const accessToken = localStorage.getItem("accessToken");

    const getUserExceptCurrent = async () => {
      try {
        const response = await fetchUserExceptCurrent(accessToken);
        setUsers(response);
      } catch (error) {
        console.log("Error fetching user expect current:", error);
      }
    };
    getUserExceptCurrent();
  }, []);

  useEffect(() => {
    // Fetch recent messages for each user
    const fetchRecentMessages = async () => {
      try {
        const accessToken = localStorage.getItem("accessToken");
        const response = await api.get("/api/messages/recent-messages", {
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        });

        console.log("API Response:::", response.data);

        // store the recent messages for each user
        const messages = response.data.recentMessages.reduce((acc, message) => {
          console.log("Messages Structure:::", message);
          const userId = message.userId.toString(); // converting objectId into string
          console.log("Recent message userId:::", userId);
          console.log("Logged in user id::::", loggedInUser);

          // if (userId) {
          //   const displayMessage =
          //     userId !== loggedInUser
          //       ? `You: ${message.message}`
          //       : message.message;
          //   acc[userId] = displayMessage; // store the formatte message content for the user
          // }

          if (userId) {
            // Add 'You:' if the logged-in user is the sender
            if (userId === loggedInUser) {
            } else {
              acc[userId] = message.message;
            }
          }
          return acc;
        }, {});

        setRecentMessages(messages);
      } catch (error) {
        console.error("Error fetching recent messages:", error);
      }
    };
    fetchRecentMessages();
  }, [users, loggedInUser]);

  // Filter users based on the search input
  const filteredUsers = users.filter((user) =>
    user.username.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="sidebar">
      {/* Search Bar */}
      <div className="search-container">
        <input
          type="text"
          placeholder="Search"
          className="search-input"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Users List */}
      <div className="user-list">
        {filteredUsers.map(
          (user) => (
            (
              <div
                key={user._id}
                className={`user-item ${
                  onUserSelect?._id === user._id ? "selected" : ""
                }`}
                onClick={() => setOnUserSelected(user)} // passing selected user
              >
                <div className="user-avatar">
                  {/* Display initials or profile image */}
                  {user.avatar ? (
                    <img src={user.avatar} alt={user.username} />
                  ) : (
                    <span>{user.username.charAt(0).toUpperCase()}</span>
                  )}
                </div>
                <div className="user-info">
                  <h4 className="user-name">{user.username}</h4>
                  <p className="user-message">
                    {recentMessages[user._id] || "No messages yet"}
                  </p>
                </div>
                {/* <div className="user-meta">
              <span className="time">20m</span>
              {index % 3 === 0 ? (
                <span className="notification-count">3</span>
              ) : (
                <span className="message-status">✔✔</span>
              )}
            </div> */}
              </div>
            )
          )
        )}
      </div>
    </div>
  );
};

export default Sidebar;
