import React, { useEffect, useState } from "react";
import { io } from 'socket.io-client';
import api from "../Api";

import "../styles/chatContainer.css"

const socket = io("http://localhost:5000");  // this is basically backend url

const ChatContainer = ({ selectedUser, currentUser }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  useEffect(() => {
    if(selectedUser) {
      // Join a chat room
      const roomId = [currentUser._id, selectedUser._id].sort().join("-");
      socket.emit("join-room", roomId);

      // fetch chat history
      const fetchChatHistory = async () => {
        try {
          const accessToken = localStorage.getItem('accessToken');
          const response = await api.get(`/api/messages/${selectedUser._id}`, {withCredentials: true}, {
            headers: {
              "Authorization": `Bearer ${accessToken}`
            }
          });
          // const data = await response.json();
          setMessages(response.data.messages);
        } catch (error) {
          console.error("Error fetching chat history:", error);
        }
      };
      fetchChatHistory();

      // listen for new messages
      socket.on('receive-message', (data) => {
        setMessages((prevMessages) => [...prevMessages, data]);
      });
    }
  }, [selectedUser]);

  // Handle send message button
  const handleSendMessage = () => {
    if(!newMessage.trim()) return;
    
    const messageData = {
      roomId: [currentUser._id, selectedUser._id].sort().join("-"),
      senderId: currentUser._id,
      receiverId: selectedUser._id,
      content: newMessage,
    };

    // Emit message via socket.io
    socket.emit('send-message', messageData);

    // Add the message locally
    setMessages((prevMessages) => [...prevMessages, messageData]);
    setNewMessage("");
  };

  if(!selectedUser) {
    return <div className="chat-container">Select a user to start chatting!</div>
  }

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h3>{selectedUser.username}</h3>
      </div>

      <div className="chat-messages">
        {messages.map((message, index) => (
          <div 
            key={index}
            className={`message ${message.senderId === currentUser._id} ? "sent" : "received" `}
          >
            <p>{message.content}</p>
          </div>
        ))}
      </div>
    </div>
  )

};

export default ChatContainer;
