import React, { useEffect, useRef, useState } from "react";
import api from "../Api";
import moment from "moment";
import { v4 as uuidv4 } from "uuid";
import "../styles/chatContainer.css";
import socket from "./socket";
import { useOnlineUsers } from "../context/onlineUsersContext";
import audio_call from "../assets/call.png";
import video_call from "../assets/video-camera.png";
import info_icon from "../assets/info.png";
import audio_icon from "../assets/mic.png";
import media_icon from "../assets/image-gallery.png";

const ChatContainer = ({ selectedUser, currentUser }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [messageType, setMessageType] = useState('text'); // default to text
  const [content, setContent] = useState("");
  const [file, setFile] = useState(null);
  const [caption, setCaptions] = useState("");
  const [isUploading, setIsUploading] = useState(false);
  const [error, setError] = useState("");
  const messageEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const onlineUsers = useOnlineUsers();

  useEffect(() => {
    if (selectedUser) {
      // Join a chat room
      const roomId = [currentUser._id, selectedUser._id].sort().join("-");
      socket.emit("join-room", roomId);

      const fetchChatHistory = async () => {
        try {
          const accessToken = localStorage.getItem("accessToken");
          const response = await api.get(`/api/messages/${selectedUser._id}`, {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          });
          setMessages(response.data.messages);
        } catch (error) {
          console.error("Error fetching chat history:", error);
        }
      };
      fetchChatHistory();

      // fetch chat history
      const handleReceiveMessage = (data) => {
        console.log("New message received::", data);

        setMessages((prevMessages) => {
          // console.log("Previous Message:::", prevMessages);
          if (!prevMessages.some((msg) => msg._id === data._id)) {
            const updateMessages = [...prevMessages, data];
            console.log("Updated messages:::", updateMessages);
            return updateMessages;
          }
          return prevMessages;
        });
      };

      socket.on("receive-message", handleReceiveMessage);

      return () => {
        socket.off("receive-message", handleReceiveMessage);
      };
    }
  }, [selectedUser, currentUser]);

  // hande file input change
  const handleFileInputChange = (e) => {
    const selectedFile = e.target.files[0];
    if(selectedFile) {
      setFile(selectedFile);
      // handleSendMessage(selectedFile);
    }
  };

  // Trigger file input when an icon is clicked
  const handleOnClick = (type) => {
    if(type === "media" || type === "audio") {
      fileInputRef.current.click();
    }
  }

  // Handle send message button
  const handleSendMessage = async () => {
    setIsLoading(true);
    setError("");

    try {
      // Validate input based on message type
      if (messageType === "text" && !content.trim()) {
        setError("Content is required for text messages.");
        return;
      }

      if (messageType !== "text" && !file) {
        setError("File is required for non-text messages.");
        return;
      }

      // Prepare form data for non-text messages
      const formData = new FormData();
      formData.append("roomId", roomId);
      formData.append("receiverId", receiverId);
      formData.append("messageType", messageType);
      formData.append("senderId", currentUserId);

      if (messageType === "text") {
        formData.append("content", content);
      } else {
        formData.append("file", file);
        formData.append("fileName", file.name);
        formData.append("fileSize", file.size);
        formData.append("fileType", file.type);
        formData.append("caption", caption);

        // Add duration for audio and video files
        if (messageType === "audio" || messageType === "video") {
          const duration = await getMediaDuration(file);
          formData.append("duration", duration);
        }

        // Add thumbnail for photo and video files
        if (messageType === "photo" || messageType === "video") {
          const thumbnail = await generateThumbnail(file);
          formData.append("thumbnailUrl", thumbnail);
        }
      }

      // Send the message to the server
      const response = await axios.post("/api/messages", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      console.log("Message sent successfully:", response.data);
      alert("Message sent!");

      // Reset form after sending
      setContent("");
      setFile(null);
      setCaption("");
    } catch (error) {
      console.error("Error sending message:", error);
      setError("Failed to send message. Please try again.");
    } finally {
      setIsUploading(false);
    }
  };

  // const handleSendMessage = async (fileToSend = file) => {
  //   if (!newMessage.trim() && !fileToSend) return;

  //   const messageId = uuidv4();
  //   const messageData = {
  //     _id: messageId,
  //     roomId: [currentUser._id, selectedUser._id].sort().join("-"),
  //     senderId: currentUser._id,
  //     receiverId: selectedUser._id,
